select DISTINCT pa.supplier_id,pa.scenic_id,pa.id,pa.state,pa.name,pa.code,pa.start_time,pa.end_time from prod_screeings pa inner join ( select distinct scenic_id , supplier_id, create_time from prod_screeings where state = 1 and supplier_id=#{supplierId} order by create_time desc
limit ${pageEntity.startIndex},${pageEntity.pageSize}
) pb on pa.scenic_id=pb.scenic_id and pa.supplier_id=pb.supplier_id order by pb.create_time desc

